sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var isValidCodFisc=validateCodFiscale(current.codice_f);
	if(isValidCodFisc==false){
		gs.addErrorMessage('Il parametro inserito come codice fiscale risulta non conforme con le regex');
		current.setAbortAction(true);
	}

})(current, previous);
